def token = "Bearer " + getTokenTestCase.getProperty('GetTokenResult').getValue();
// Iterate through teststeps
for(i=0;i<testSteps.size();i++){
    // Set CorrelationId
    def requestPropertValue = testSteps[i].getProperty('Request').getValue();
    def xmlRequest = groovyUtils.getXmlHolder(requestPropertValue);
    def guid = "${java.util.UUID.randomUUID()}";
    xmlRequest.setNodeValue("declare namespace ns='http://service.matrix.financials.fivedegrees.nl/1.0'; //ns:correlationId", guid);
    testSteps[i].setPropertyValue('Request', xmlRequest.getXml());
    // Set Authorization header for test request
    def headers = new StringToStringMap()
    headers.put("Authorization",token)
    testSteps[i].testRequest.setRequestHeaders(headers);
}

